            Equ_PatrolPlanItemDP ee = new Equ_PatrolPlanItemDP();
            string strSQL = string.Empty;
            OracleConnection cn = ConfigTool.GetConnection();
            strSQL = "SELECT * FROM Equ_PatrolPlanItem WHERE ID = " + lngID.ToString();
