        #region 批量保存计划项 SavePlanDetailItem
        /// <summary>
        /// 
        /// </summary>
        /// <param name="dt"></param>
        /// <param name="pPlanID"></param>
        /// <param name="pPlanName"></param>
        public void SavePlanDetailItem(DataTable dt, decimal pPlanID,string pPlanName)
        {
            OracleConnection cn = ConfigTool.GetConnection();
            if (cn.State != ConnectionState.Open) { cn.Open(); }
            OracleTransaction tran = cn.BeginTransaction();

            string strSQL = string.Empty;
            strSQL = "Delete Equ_PatrolPlanItem Where PlanID=" + pPlanID.ToString();
            OracleDbHelper.ExecuteNonQuery(tran, CommandType.Text, strSQL);

            foreach (DataRow dr in dt.Rows)
            {
                string sID = EPGlobal.GetNextID(tran, "Equ_PatrolPlanItemID").ToString();
                strSQL = @" INSERT INTO Equ_PatrolPlanItem(
                                    ID,
									PlanID,
									PlanName,
									EquID,
									EquName,
									ItemID,
									ItemName
					)
					VALUES( " + sID + "," +
                           pPlanID.ToString() + "," +
                           StringTool.SqlQ(pPlanName) + "," +
                           dr["EquID"].ToString() + "," +
                           StringTool.SqlQ(dr["EquName"].ToString()) + "," +
                           dr["ItemID"].ToString() + "," +
                           StringTool.SqlQ(dr["ItemName"].ToString()) + 
                   ")";
                OracleDbHelper.ExecuteNonQuery(tran, CommandType.Text, strSQL);
            }
            try
            {
                //if (!string.IsNullOrEmpty(strSQL))
                //    OracleDbHelper.ExecuteNonQuery(tran, CommandType.Text, strSQL);
                tran.Commit();
            }
            catch (Exception e)
            {
                tran.Rollback();
                throw e;
            }
            finally
            {
                tran.Dispose();
                ConfigTool.CloseConnection(cn);
            }
        }
        #endregion 

